import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class testComponent {

  constructor(private http: HttpClient,private router: Router, @Inject('BASE_URL') private baseUrl: string) 
  { 
    this.prueba_get();
  }

  prueba_get()
  {
    this.http.get<Equipo[]>(this.baseUrl + 'equipo/seeEquipos').subscribe(
      result=>{
        console.log(result);
      }, error=>{console.log("error");})
  }

  prueba_post(Id_equipo: number, Codigo_Equipo: string, Nombre_equipo: string, Estado_Equipo: string, Detalle_equipo: string)
  {

    const nuevoEquipo = {
      Id_equipo,
      Codigo_Equipo,
      Nombre_equipo,
      Estado_Equipo,
      Detalle_equipo
    };

    this.http.post<string>(this.baseUrl + 'equipo/addEquipos', nuevoEquipo).subscribe(
      result=>{
        console.log(result);
        alert('Equipo registrado con éxito');
      }, error=>{
        console.log("error");
        alert('Error al registrar el equipo');})
  }
}

interface Equipo
{
  Id_equipo: number;
  Codigo_Equipo: string;
  Nombre_equipo: string
  Estado_Equipo: string;
  Detalle_equipo: string;
}
