import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isExpanded = false;

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}

<body>
  <header>
    <nav class="navbar">
      <div class="container">
        <div class="navbar-left">
          <img src="logo.png" alt="Logo UCB" class="navbar-logo">
        </div>

        <div class="navbar-center">
          <div class="search-bar">
            <input type="text" placeholder="Buscar" class="search-input">
            <button class="search-button">
              <img src="search-icon.png" alt="Buscar">
            </button>
          </div>
        </div>

        <div class="navbar-right">
          <img src="notification-icon.png" alt="Notificaciones" class="icon">
          <img src="user-icon.png" alt="Usuario" class="icon">
          <img src="settings-icon.png" alt="Configuración" class="icon">
        </div>
      </div>
    </nav>
  </header>
</body>
