import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
}
<div style="width: vmax; height: 100vmax;">
  <img src="../../assets/images/banner.png" alt="banner">
  <h1 style="font-size: 48px; margin-left: 30px; margin-top: 4%; margin-bottom: 3%;" class="montserrat">Cursos mas vistos!</h1>
  <ul>
    <li class="category-card">
      <img src="../../assets/images/card_image.png" alt="card_image">
      <h2>Title</h2>
      <h6>Theme</h6>
      <h6>Date</h6>
    </li>
  </ul>
</div>
