import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isExpanded = false;

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
<body>
  <header>
    <nav class="navbar">
      <div class="container">
        <div class="navbar-left">
          <a href="">
            <img src="../../assets/icons/LogoUCB.png" alt="Logo UCB" class="navbar-logo">
          </a>
          <img src="../../assets/icons/Videos Tendencia.png" alt="Videos" class="navbar-logo">
          <img src="../../assets/icons/Agregar Video.png" alt="Creación" class="navbar-logo">
        </div>

        <div class="navbar-center">
          <div class="search-bar">
            <input type="text" placeholder="Buscar" class="search-input">
            <button class="search-button">
              <img src="../../assets/icons/Search.png" alt="Buscar">
            </button>
          </div>
        </div>

        <div class="navbar-right">
          <img src="../../assets/icons/Notificacion.png" alt="Notificaciones" class="icon">
          <img src="../../assets/icons/Perfil.png" alt="Usuario" class="icon">
          <img src="../../assets/icons/Configuracion.png" alt="Configuración" class="icon">
        </div>
      </div>
    </nav>
  </header>
</body>
